SELECT o._id as observation_id, d.Taxon_FullName, d.Recorded_as_FullName, d.Taxon_id, d.Taxon_redlist_status, o.observationDate, o.decimalLatitude, o.decimalLongitude, o.accuracy, l.name as locality, l.utm10, GROUP_CONCAT(p.DKandLatinName SEPARATOR ', ') as host, s.name as Substrate, v.name as VegetationType, uf.name as ReportedBy,GROUP_CONCAT(ouu.name SEPARATOR ', ') as Leg, o.verbatimLeg, ud.name as Det, d.Determination_verbatimdeterminator,  d.Determination_validation
FROM Observation o JOIN Locality l ON l._id = o.locality_id LEFT JOIN DeterminationView2 d ON o._id = d.Determination_observation_id LEFT JOIN Users ud ON d.Determination_user_id = ud._id LEFT JOIN Users uf ON o.primaryuser_id = uf._id LEFT JOIN Substrate s ON o.substrate_id = s._id LEFT JOIN VegetationType v ON o.vegetationtype_id = v._id LEFT JOIN ObservationPlantTaxon op ON op.observation_id=o._id LEFT JOIN PlantTaxon p ON op.planttaxon_id=p._id 
LEFT JOIN ObservationUsers ou ON ou.observation_id = o._id LEFT JOIN Users ouu ON ou.user_id = ouu._id
GROUP BY o._id;
